    private void Awake()
    {
        level_id = staticVariables.staticVar.get_level_id();
        skies = main_camera.GetComponent<Skybox>();
        initialiseGraphics(level_id);
    }

    // Start is called before the first frame update
    void Start()
    {
        apple_combo = 0;
        Time.timeScale = 1;
        llama_position = 2.0f;
        llama_position_front = 3.8f;
        spawn_pos = 30.0f;
        speedZ = 10;
        cur_apple = 0;
        cur_rotten_apple = 0;
        time = 0.0f;
        instance = this;
        ScoreText.text = "0";
        score = 0;
        time_to_arrive = (spawn_pos - llama_position_front) / speedZ;

        sounds = GetComponents<AudioSource>();
        hit_sound = sounds[0];
        hit_sound.volume *= PlayerPrefs.GetFloat("volume", .5f);
        
        bomb_sound = sounds[1];
        bomb_sound.volume *= PlayerPrefs.GetFloat("volume", .5f);

        isGameActive = true;
        hiscore = PlayerPrefs.GetInt("hiscore"+level_id, 0);

        Debug.Log("Loaded level " + level_id + ", which has " + apples_in_level[level_id] + " apples.");
        sounds[2 + level_id].Play();
        sounds[2 + level_id].volume *= PlayerPrefs.GetFloat("volume", .5f);
        Debug.Log("Hit vol " + hit_sound.volume + ", bomb vol " + bomb_sound.volume + ", song vol "+ sounds[2 + level_id].volume);
    }

    // Update is called once per frame
    void Update()
    {
        HiScoreText.text = "" + hiscore;
        ScoreText.text = "" + score;
        Multiplier.text = "x" + scoreMultiplier;
        time += Time.deltaTime;

        if (isGameActive)
        {
            congratulations.SetActive(false);
        }

        if (cur_rotten_apple < rotten_apple_array.GetLength(1))
        {
            if (rotten_apple_array[level_id, cur_rotten_apple] == 0.0f)
                cur_rotten_apple = rotten_apple_array.GetLength(1);
            else if (time >= rotten_apple_array[level_id, cur_rotten_apple] - time_to_arrive)
            {
                Instantiate(rottenAppleprefab, new Vector3(rotten_apple_xpos[level_id, cur_rotten_apple], 0.7f, 30.0f), Quaternion.Euler(apple_rotation));
                cur_rotten_apple++;
            }
        }

        if (cur_apple < apple_array.GetLength(1))
        {
            if (apple_array[level_id, cur_apple] == 0.0f)
                cur_apple = apple_array.GetLength(1);
            else if (time >= apple_array[level_id, cur_apple] - time_to_arrive)
            {
                Instantiate(appleprefab, new Vector3(apple_xpos[level_id, cur_apple], 0.7f, 30.0f), Quaternion.Euler(apple_rotation));
                cur_apple++;
            }
        }

        if (!sounds[2 + level_id].isPlaying && (isGameActive == true) && (isPaused == false))
        {
            isGameActive = false;
            if (score > hiscore)
            {
                hiscore = score;
                PlayerPrefs.SetInt("hiscore" + level_id, hiscore);
            }
            Time.timeScale = 0;
            congratulations.SetActive(true);
            congrats.Congrats.stars_update(level_id);
            if(level_id == 3)
                SceneManager.LoadScene("CutsceneEnd");
        }
    }

    public void AppleHit()
    {
        apple_combo += 1;
        if (apple_combo >= 80)
        {
            scoreMultiplier = 8;
        }
        else if(apple_combo >= 20)
        {
            scoreMultiplier = 4;
        }

        score += 1*scoreMultiplier;
        hit_sound.Play();

        progressBar.progress.IncrementProgress(1.0f/ apples_in_level[level_id]);
    }

    public void RottenAppleHit()
    {
        
        apple_combo = 0;
        scoreMultiplier = 1;
        Multiplier.text = "x" + scoreMultiplier;

        Debug.Log("After rotten collision -> entered game manager!");
        bomb_sound.Play();
        lifeSystem.lifesystem.LifeRemoved(1);
    }

    //FOR GAME OVER
    public void GameOver()
    {
        isGameActive = false;

     
        gameover.SetActive(true);
        //level1.SetActive(false);
        sounds[2 + level_id].Stop();
        Time.timeScale = 0;
       
        if (score > hiscore)
        {
          hiscore = score;
          PlayerPrefs.SetInt("hiscore"+level_id, hiscore);
        }
    }

    public void RestartGame()
    {
        SceneManager.LoadScene(SceneManager.GetActiveScene().name);
    }

    public void backtomenu()
    {
        SceneManager.LoadScene("mainMenu");
    }

    public void initialiseGraphics(int level_id)
    {
        if (level_id == 0) //LEVEL 1
        {
            mountains[0].SetActive(true);
            mountains[1].SetActive(true);
            terrain.SetActive(true);
            exoticterrain.SetActive(false);
            fence.SetActive(true);
            level3.SetActive(false);
            camera2.SetActive(false);
            camera3.SetActive(false);
            palmtrees.SetActive(false);
            cloud_fence.SetActive(false);
            for (int i = 0; i < 6; i++)
            {
                clouds[i].SetActive(false);
            }
        }
        else if(level_id == 1) //LEVEL 2
        {
            mountains[0].SetActive(false);
            mountains[1].SetActive(false);
            terrain.SetActive(false);
            exoticterrain.SetActive(false);
            fence.SetActive(false);
            level3.SetActive(false);
            skies.enabled = true;
            palmtrees.SetActive(false);
            cloud_fence.SetActive(true);
            stars.SetActive(false);
            for (int i = 0; i < 6; i++)
            {
                clouds[i].SetActive(true);
            }
        }
        else if (level_id == 2) //LEVEL 3
        {
            mountains[0].SetActive(false);
            mountains[1].SetActive(false);
            terrain.SetActive(false);
            exoticterrain.SetActive(false);
            camera2.SetActive(true);
            main_camera.SetActive(false);
            skies.enabled = false;
            stars.SetActive(true);
            level3.SetActive(false);
            fence.SetActive(false);
            camera3.SetActive(false);
            palmtrees.SetActive(false);
            cloud_fence.SetActive(false);
            for (int i = 0; i < 6; i++)
            {
                clouds[i].SetActive(false);
            }
        }
        else if (level_id == 3)
        {
            mountains[0].SetActive(false);
            mountains[1].SetActive(false);
            exoticterrain.SetActive(true);
            terrain.SetActive(false);
            fence.SetActive(false);
            main_camera.SetActive(false);
            skies.enabled = false;
            level3.SetActive(true);
            camera2.SetActive(false);
            palmtrees.SetActive(true);
            cloud_fence.SetActive(false);
            stars.SetActive(false);
            for (int i = 0; i < 6; i++)
            {
                clouds[i].SetActive(false);
            }
            camera3.SetActive(true);
        }
    }

    public void resumeGame()
    {
        if (isPaused)
        {
            button_back.SetActive(false);
            button_resume.SetActive(false);
            Time.timeScale = 1;
            isPaused = false;
            sounds[2 + level_id].Play();
        }
    }

    public void pauseGame() { 
        if(!isPaused)
        {
            button_back.SetActive(true);
            button_resume.SetActive(true);
            Time.timeScale = 0;
            isPaused = true;
            sounds[2 + level_id].Pause();
        }
    }


